SELECT , d.price AS paymentPrice, c.parent_id AS categoryId FROM sb_order a LEFT JOIN sb_category c ON a.category_id = c.id LEFT JOIN sb_payment d ON a.order_no = d.order_sn WHERE a.member_id = #{memberId} AND a.del_flag = '0'
AND a.status_ IN (0,1)
AND a.status_ = #{status}
ORDER BY a.create_date DESC LIMIT #{sizeStart} , #{size}

SELECT , b.name_ AS Recyclers_name, b.tel AS BTel, f.num, p.price AS paymentPrice FROM sb_order a LEFT JOIN sb_category c ON a.category_id = c.id LEFT JOIN sb_payment p ON p.order_sn = a.order_no LEFT JOIN sb_recyclers b ON a.recycler_id = b.id LEFT JOIN ( SELECT d.order_id, max(d.num) AS num FROM sb_arrival_time_log d LEFT JOIN sb_order a ON d.order_id = a.id GROUP BY d.order_id ) f ON a.id = f.order_id WHERE a.del_flag = '0' AND a.company_id =#{companyId} AND a.is_scan = #{isScan}
AND a.status_ in
#{item}
AND a.order_no LIKE concat(concat('%',#{orderNo}),'%')
AND a.link_man LIKE concat(concat('%',#{linkMan}),'%')
and a.express_name LIKE concat(concat('%',#{recyclersName}),'%')
AND b.name_ LIKE concat(concat('%',#{recyclersName}),'%')
AND a.complete_date >= CONCAT(#{startTime},' 00:00:00')
AND a.complete_date <= CONCAT(#{endTime},' 23:59:59')
AND a.title = #{title}
ORDER BY a.create_date DESC LIMIT #{startSize} , #{pageSize}

SELECT so.id AS orderId, so.create_date AS creatDate, so.address, so.link_man AS linkName, so.tel, so.full_address, so.status_, FORMAT(so.price, 2) AS price, so.ach_price AS achPrice, so.order_no orderNo, date_format(IF(ISNULL(so.arrival_time),"",so.arrival_time), '%Y-%m-%d') AS arrivalTime, so.arrival_period, sc.name_ AS cateName, sc.icon, so.complete_date completeDate, so.title, so.community_id, so.company_id, so.is_cash, so.order_remarks, sr.is_manager FROM sb_order so LEFT JOIN sb_category sc ON so.category_id = sc.id LEFT JOIN sb_recyclers sr ON sr.id = so.recycler_id WHERE so.del_flag = '0' and so.title != 4
AND so.recycler_id = #{orderBean.recyclerId}
AND so.status_ = #{orderBean.status}
ORDER BY so.complete_date DESC
ORDER BY so.arrival_time ASC,so.arrival_period ASC,so.create_date ASC
ORDER BY so.create_date DESC
LIMIT #{startSize} , #{pageSize}

SELECT count(*) FROM sb_order so LEFT JOIN sb_category sc ON so.category_id = sc.id WHERE so.del_flag = '0' and so.title != 4
AND so.recycler_id = #{orderBean.recyclerId}
AND so.status_ = #{orderBean.status}

SELECT COUNT(*) FROM ( SELECT so.id AS orderId, so.create_date AS creatDate, so.address, so.link_man AS linkName, so.tel, so.status_, so.price, so.order_no orderNo, IF ( ISNULL(so.arrival_time), "", so.arrival_time ) AS arrivalTime, sc.name_ AS cateName, sc.icon, so.complete_date completeDate FROM sb_category sc INNER JOIN sb_order so ON so.category_id = sc.id WHERE so.del_flag = '0' and so.title != 4
AND so.recycler_id = #{orderBean.recyclerId}
) scso LEFT JOIN sb_order_log sol ON scso.orderId = sol.order_id WHERE sol.op_status_after = 'CANCELTASK'

SELECT scomco.order_no AS orderNo, sc.name_ AS cateName, sc.icon, scomco.address, scomco.full_address AS fullAddress, date_format(IF(ISNULL(scomco.arrival_time),"",scomco.arrival_time), '%Y-%m-%d') AS arrivalTime, scomco.complete_date AS completeTime, scomco.link_man AS linkName, scomco.tel, scomco.id AS orderId, FORMAT(scomco.price, 2) AS price, scomco.ach_price AS achPrice, scomco.comTel, scomco.comName, scomco.status_, scomco.arrival_period AS arrivalPeriod, scomco.remarks, scomco.title, scomco.community_id, scomco.company_id, scomco.ach_remarks, scomco.is_cash, scomco.green_count, sr.is_manager FROM ( SELECT scom.name_ AS comName, scom.tel AS comTel, so.* FROM sb_company scom INNER JOIN sb_order so ON so.company_id = scom.id WHERE so.del_flag = '0'
AND so.id = #{orderBean.id}
) scomco LEFT JOIN sb_category sc ON sc.id = scomco.category_id LEFT JOIN sb_recyclers sr ON sr.id = scomco.recycler_id

SELECT soe.score, soe.content, soe.create_by AS createBy, soe.create_date AS createDate FROM sb_order_evaluation soe WHERE soe.del_flag = '0' AND soe.recycler_id = #{scoreAppBean.recyclerId}
AND soe.score in
#{item}
ORDER BY soe.create_date DESC LIMIT #{startSize} , #{pageSize}

SELECT count(*) FROM sb_order_evaluation soe WHERE soe.del_flag = '0' AND soe.recycler_id = #{scoreAppBean.recyclerId}
AND soe.score in
#{item}

SELECT IFNULL(count(1),0) AS count_ FROM ( SELECT so.id AS orderId, so.create_date AS creatDate, so.address, so.link_man AS linkName, so.tel, so.full_address, 6 AS status_, so.price, so.ach_price, so.order_no orderNo, date_format(
IF ( ISNULL(so.arrival_time), "", so.arrival_time ), '%Y-%m-%d' ) AS arrivalTime, so.arrival_period AS arrivalPeriod, sc.name_ AS cateName, sc.icon, so.complete_date completeDate, so.title, so.community_id, so.company_id, so.is_cash, so.order_remarks, sr.is_manager FROM sb_category sc INNER JOIN sb_order so ON so.category_id = sc.id LEFT JOIN sb_recycle_cancel_log srcl ON srcl.order_id = so.id LEFT JOIN sb_recyclers sr ON sr.id = so.recycler_id WHERE so.del_flag = '0' AND srcl.recycler_id = #{orderBean.recyclerId} AND so.recycler_id = #{orderBean.recyclerId}
AND so.tel like #{orderBean.tel}
UNION SELECT so.id AS orderId, so.create_date AS creatDate, so.address, so.link_man AS linkName, so.tel, so.full_address, so.status_, FORMAT(so.price, 2) AS price, so.ach_price AS achPrice, so.order_no orderNo, date_format(
IF ( ISNULL(so.arrival_time), "", so.arrival_time ), '%Y-%m-%d' ) AS arrivalTime, so.arrival_period, sc.name_ AS cateName, sc.icon, so.complete_date completeDate, so.title, so.community_id, so.company_id, so.is_cash, so.order_remarks, sr.is_manager FROM sb_order so LEFT JOIN sb_category sc ON so.category_id = sc.id LEFT JOIN sb_recyclers sr ON sr.id = so.recycler_id WHERE so.del_flag = '0' AND so.recycler_id = #{orderBean.recyclerId}
AND so.tel like #{orderBean.tel}
) countALL WHERE 1 = 1;

SELECT * FROM ( SELECT so.id AS orderId, so.create_date AS creatDate, so.address, so.link_man AS linkName, so.tel, so.full_address, so.status_, FORMAT(so.price, 2) AS price, so.ach_price AS achPrice, so.order_no orderNo, date_format(
IF ( ISNULL(so.arrival_time), "", so.arrival_time ), '%Y-%m-%d' ) AS arrivalTime, so.arrival_period, sc.name_ AS cateName, sc.icon, so.complete_date completeDate, so.title, so.community_id, so.company_id, so.is_cash, so.order_remarks, sr.is_manager FROM sb_order so LEFT JOIN sb_category sc ON so.category_id = sc.id LEFT JOIN sb_recyclers sr ON sr.id = so.recycler_id WHERE so.del_flag = '0' AND so.recycler_id = #{orderBean.recyclerId}
AND so.tel like #{orderBean.tel}
UNION SELECT so.id AS orderId, so.create_date AS creatDate, so.address, so.link_man AS linkName, so.tel, so.full_address, 6 AS status_, so.price, so.ach_price, so.order_no orderNo, date_format(
IF ( ISNULL(so.arrival_time), "", so.arrival_time ), '%Y-%m-%d' ) AS arrivalTime, so.arrival_period AS arrivalPeriod, sc.name_ AS cateName, sc.icon, so.complete_date completeDate, so.title, so.community_id, so.company_id, so.is_cash, so.order_remarks, sr.is_manager FROM sb_category sc INNER JOIN sb_order so ON so.category_id = sc.id LEFT JOIN sb_recycle_cancel_log srcl ON srcl.order_id = so.id LEFT JOIN sb_recyclers sr ON sr.id = so.recycler_id WHERE so.del_flag = '0' AND srcl.recycler_id = #{orderBean.recyclerId} AND so.recycler_id = #{orderBean.recyclerId}
AND so.tel like #{orderBean.tel}
) countALL WHERE 1 = 1 group by countALL.orderId ORDER BY countALL.creatDate DESC LIMIT #{startSize} , #{pageSize}

SELECT COUNT(1) FROM ( SELECT b.* FROM sb_recyclers a LEFT JOIN sb_order b ON a.id = b.recycler_id LEFT JOIN sb_category c ON c.id = b.category_id WHERE a.parents_id = #{recycleId} AND b.title = 4 AND b.status_ = 1 AND a.del_flag = 0 AND b.del_flag = 0 AND b.is_scan = 0
AND b.tel like #{tel}
UNION SELECT b.* FROM sb_order b LEFT JOIN sb_recyclers a ON a.id = b.recycler_id LEFT JOIN sb_category c ON c.id = b.category_id WHERE b.recycler_id = #{recycleId} AND b.title = 4 AND a.del_flag = 0 AND b.del_flag = 0 AND b.is_scan = 0
AND b.tel like #{tel}
) countAll

SELECT * FROM ( SELECT a.id AS recyclerId, a.name_ AS recyclerName, a.tel AS recyclerMobile, a.is_manager AS isManager, a.ali_user_id AS aliUserId, b.id, b.title, b.status_ AS orderStatus, b.link_man AS userName, b.tel AS userMobile, b.address AS address, b.price, b.ach_price AS achPrice, b.is_cash AS isCash, b.order_no AS orderNo, b.category_id AS categoryId, b.order_remarks AS orderRemarks, c.name_ AS categoryName, c.icon AS icon, date_format( b.create_date, '%Y-%m-%d %H:%i:%S' ) AS createDate, CONCAT( date_format(b.arrival_time, '%Y-%m-%d'),
IF ( b.arrival_period = 'am', ' 上午', IF(b.arrival_period = 'pm','下午',CONCAT(' ',b.arrival_period)) ) ) AS arrivalTime, date_format( b.cancel_time, '%Y-%m-%d %H:%i:%S' ) AS cancelTime, date_format( b.receive_time, '%Y-%m-%d %H:%i:%S' ) AS receiveTime, date_format( b.distribute_time, '%Y-%m-%d %H:%i:%S' ) AS distributeTime, date_format( b.complete_date, '%Y-%m-%d %H:%i:%S' ) AS completeDate,
IF ( now() > CONCAT( date_format(b.arrival_time, '%Y-%m-%d'),
IF ( b.arrival_period = 'pm', ' 12:00:00', IF(b.arrival_period = 'am',' 00:00:01',CONCAT(' ',SUBSTR(b.arrival_period,1,2) ,':00:00')) ) ), 'Y', 'N' ) AS isRead FROM sb_recyclers a LEFT JOIN sb_order b ON a.id = b.recycler_id LEFT JOIN sb_category c ON c.id = b.category_id WHERE a.parents_id = #{recycleId} AND b.title = 4 AND b.status_ = 1 AND a.del_flag = 0 AND b.del_flag = 0 AND b.is_scan = 0
AND b.tel like #{tel}
UNION SELECT a.id AS recyclerId, a.name_ AS recyclerName, a.tel AS recyclerMobile, a.is_manager AS isManager, a.ali_user_id AS aliUserId, b.id, b.title, b.status_ AS orderStatus, b.link_man AS userName, b.tel AS userMobile, b.address AS address, b.price, b.ach_price AS achPrice, b.is_cash AS isCash, b.order_no AS orderNo, b.category_id AS categoryId, b.order_remarks AS orderRemarks, c.name_ AS categoryName, c.icon AS icon, date_format( b.create_date, '%Y-%m-%d %H:%i:%S' ) AS createDate, CONCAT( date_format(b.arrival_time, '%Y-%m-%d'),
IF ( b.arrival_period = 'am', ' 上午', IF(b.arrival_period = 'pm','下午',CONCAT(' ',b.arrival_period)) ) ) AS arrivalTime, date_format( b.cancel_time, '%Y-%m-%d %H:%i:%S' ) AS cancelTime, date_format( b.receive_time, '%Y-%m-%d %H:%i:%S' ) AS receiveTime, date_format( b.distribute_time, '%Y-%m-%d %H:%i:%S' ) AS distributeTime, date_format( b.complete_date, '%Y-%m-%d %H:%i:%S' ) AS completeDate,
IF ( now() > CONCAT( date_format(b.arrival_time, '%Y-%m-%d'),
IF ( b.arrival_period = 'pm', ' 12:00:00', IF(b.arrival_period = 'am',' 00:00:01',CONCAT(' ',SUBSTR(b.arrival_period,1,2) ,':00:00')) ) ), 'Y', 'N' ) AS isRead FROM sb_order b LEFT JOIN sb_recyclers a ON a.id = b.recycler_id LEFT JOIN sb_category c ON c.id = b.category_id WHERE b.recycler_id = #{recycleId} AND b.title = 4 AND a.del_flag = 0 AND b.del_flag = 0 AND b.is_scan = 0
AND b.tel like #{tel}
) countAll WHERE 1 = 1 ORDER BY countALL.creatDate DESC LIMIT #{startPage},#{endPage}
