mapper.select:
SELECT DISTINCT b.id, b.name_ AS name, b.tel, a.card_type as cardType, f.house_name AS houseName, d.community_name AS communityName, d.street_name AS streetName, d.area_name AS areaName, d.city_name AS cityName, d.province_name AS provinceName FROM sb_company_recycler a LEFT JOIN sb_recyclers b ON a.recycler_id = b.id LEFT JOIN sb_recyclers_range c ON a.company_id = c.company_id AND a.recycler_id = c.recyclers_id LEFT JOIN sb_company_community d ON d.id = c.community_id LEFT JOIN sb_community_house_name f ON f.id = c.community_house_id WHERE a.company_id = #{companyId} AND a.status_ = '1'
AND a.card_type = #{cardType}
AND b.tel LIKE CONCAT('%',#{tel},'%')
AND b.name_ LIKE CONCAT('%',#{name},'%')
ORDER BY a.create_date DESC LIMIT #{pageStart},#{pageSize}
