mapper.select:
SELECT sc.name_ as comName, sc.id, scr.status_ FROM sb_company sc INNER JOIN sb_company_recycler scr ON sc.id = scr.company_id WHERE scr.recycler_id = #{recyclerId}
and scr.type_ = '4'
and scr.type_ = '1'

SELECT DISTINCT sccco.company_id AS id, sc.name_ AS cateName FROM ( SELECT scc.category_id, scc.company_id FROM sb_company_category scc WHERE scc.del_flag = '0' AND scc.company_id IN
#{item}
) sccco LEFT JOIN sb_category sc ON sc.id = sccco.category_id

    	select  sr.id ,sr.name_ as recyclerName,sr.id_card as idCard,sr.tel as telephone,scr.del_flag as delflag from  sb_company_recycler  scr
        inner join  sb_recyclers sr
        on sr.id=scr.recycler_id
        inner join  sb_company sc
		on sc.id = scr.company_id
		where scr.status_='1' and sc.id= #{recyclerBean.companyId}

         and  sr.name_ =#{recyclerBean.recyclerName}



         and  sr.id_card =#{recyclerBean.idCard}

       ORDER BY scr.update_date desc
