mapper.select:
SELECT
	*
FROM
	${memberAddress.tableName}
WHERE
	1=1

	AND id = #{memberAddress.id}


	AND	ali_user_id = #{memberAddress.aliUserId}


	AND member_id = #{memberAddress.memberId}


	AND town_code = #{memberAddress.townCode}


	AND city_id = #{memberAddress.cityId}


	AND area_id = #{memberAddress.areaId}


	AND street_id = #{memberAddress.streetId}


	AND community_id = #{memberAddress.communityId}


	AND comm_by_user_input = #{memberAddress.commByUserInput}


	AND address_ = #{memberAddress.address}


	AND house_number = #{memberAddress.houseNumber}


	AND tel = #{memberAddress.tel}


	AND is_selected = #{memberAddress.isSelected}


	AND del_flag = #{memberAddress.delFlag}


	AND name_ = #{memberAddress.name}


	AND city_name = #{memberAddress.cityName}


	AND area_name = #{memberAddress.areaName}


	AND street_name = #{memberAddress.streetName}


	AND community_name = #{memberAddress.communityName}


	AND map_name = #{memberAddress.mapName}


	AND map_address = #{memberAddress.mapAddress}

order by is_selected desc

mapper.delete:
DELETE
FROM
	${memberAddress.tableName}
WHERE
	1 = 1

	AND id = #{memberAddress.id}


	AND	ali_user_id = #{memberAddress.aliUserId}


	AND member_id = #{memberAddress.memberId}


	AND town_code = #{memberAddress.townCode}


	AND city_id = #{memberAddress.cityId}


	AND area_id = #{memberAddress.areaId}


	AND street_id = #{memberAddress.streetId}


	AND community_id = #{memberAddress.communityId}


	AND comm_by_user_input = #{memberAddress.commByUserInput}


	AND address_ = #{memberAddress.address}


	AND house_number = #{memberAddress.houseNumber}


	AND tel = #{memberAddress.tel}


	AND is_selected = #{memberAddress.isSelected}


	AND del_flag = #{memberAddress.delFlag}


	AND name_ = #{memberAddress.name}


	AND version_ = #{memberAddress.version}


	AND city_name = #{memberAddress.cityName}


	AND area_name = #{memberAddress.areaName}


	AND street_name = #{memberAddress.streetName}


	AND community_name = #{memberAddress.communityName}


	AND map_name = #{memberAddress.mapName}


	AND map_address = #{memberAddress.mapAddress}
